<?phpApp::uses('UsersAppModel', 'Users.Model'); /** * User * * @category Model * @package  Croogo.Users.Model * @version  1.0 * @author   Fahad Ibnay Heylaal <contact@fahad19.com> * @license  http://www.opensource.org/licenses/mit-license.php The MIT License * @link     http://www.croogo.org */class LessonPayment extends UsersAppModel {/** * Model name * * @var string * @access public */	public $name = 'LessonPayment';  /** * Validation * * @var array * @access public */	public $validate = array(  	);	public function checkduepayment($userid){		App::import("Model", "Users.LessonPayment");  		 		$model = new LessonPayment(); 		$r = $model->find('first',array('conditions'=>array('student_id'=>$userid,'lesson_take'=>1,'payment_complete'=>0)));		if(!empty($r)){			if($r['LessonPayment']['payment_complete']==0){				return $r;			}		}		return "";	}}